import React from 'react';
import { ThemeProvider } from 'styled-components';
import MessageInput from './components/MessageInput';  // Import the MessageInput component
import { theme } from './theme';  // Import your theme

function App() {
  return (
    <ThemeProvider theme={theme}>
      <div style={{ height: '100vh', display: 'flex', flexDirection: 'column', justifyContent: 'space-between' }}>
        <h1 style={{ textAlign: 'center' }}>Hello, Intellimint!</h1>
        <MessageInput onSendMessage={(message) => console.log('Message sent:', message)} />
      </div>
    </ThemeProvider>
  );
}

export default App;
