import React from 'react';
import styled from 'styled-components';

const MessageWrapper = styled.div`
  display: flex;
  justify-content: ${props => (props.isUser ? 'flex-end' : 'flex-start')};
  margin-bottom: 10px;
  padding: 0 20px;  /* Ensures the messages stay near the edges */
`;

const Message = styled.div`
  background-color: ${props => (props.isUser ? props.theme.colors.mutedGreen : props.theme.colors.primary)};
  color: ${props => props.theme.colors.text};
  padding: 10px;
  border-radius: 10px;
  max-width: 60%; /* This ensures the message isn't too wide */
  word-wrap: break-word;
`;

const MessageList = ({ messages }) => {
  return (
    <div>
      {messages.map((message, index) => (
        <MessageWrapper key={index} isUser={message.role === 'user'}>
          <Message isUser={message.role === 'user'}>
            {message.content}
          </Message>
        </MessageWrapper>
      ))}
    </div>
  );
};

export default MessageList;
