const theme = {
    colors: {
      primary: '#3D3D3D',           // Muted dark gray for general elements
      secondary: '#3D3D3D',         // Lighter gray for accents like buttons
      background: '#000000',        // Black background
      input: '#2D2D2D',             // Dark gray for input field background
      inputBackground: '#000000',   // Keep input field background consistent
      text: '#FFFFFF',              // White text
      mutedGreen: '#6B8E23',        // Muted green for the "Send" button
      buttonBackground: '#6B8E23',  // Muted green for buttons
      hoverGreen: '#556B2F',        // Slightly darker green for hover state on buttons
      border: '#555555',            // Gray border color for input fields
    }
};

export default theme;
