const theme = {
    colors: {
      primary: '#98FF98',          // Mint Green
      secondary: '#20B2AA',        // Teal
      background: '#2D2D2D',       // Neutral Dark Gray
      inputBackground: '#2D2D2D',  // Matching input background for dark theme
      text: '#F5F5F5',             // Light Gray/White for text
      highlight: '#98FF98',        // Mint green for highlights or emphasis
    }
  };

  export default theme;
