import React, { useRef, useEffect, memo } from 'react';
import styled from 'styled-components';
import Prism from 'prismjs';
import 'prismjs/themes/prism-tomorrow.css'; // Choose your theme
import Editor from 'react-simple-code-editor';

// You can import additional languages for Prism.js like this
import 'prismjs/components/prism-python';
import 'prismjs/components/prism-java';
import 'prismjs/components/prism-c'; 
// Add more languages as needed

const MessageListContainer = styled.div`
  display: flex;
  flex-direction: column;
  height: calc(100vh - 120px); // Adjust based on your header and input heights
  overflow-y: auto;
  padding: 20px;
`;

const MessageWrapper = styled.div`
  display: flex;
  justify-content: ${props => (props.isUser ? 'flex-end' : 'flex-start')};
  margin-bottom: 10px;
`;

const Message = styled.div`
  background-color: ${props => (props.isUser ? props.theme.colors.mutedGreen : props.theme.colors.primary)};
  color: ${props => props.theme.colors.text};
  padding: 10px 15px;
  border-radius: 20px;
  max-width: 60%;
  word-wrap: break-word;
  box-shadow: 0 2px 6px rgba(0, 0, 0, 0.2);
  line-height: 1.4;
  font-size: 1rem;
`;

const CodeBlock = styled.div`
  background-color: #2d2d2d;
  border-radius: 4px;
  padding: 10px;
  margin: 10px 0;
  overflow-x: auto;
`;

const MemoizedMessage = memo(({ content, isUser }) => {
  const formatContent = (text) => {
    const codeBlockRegex = /```(\w+)?\n([\s\S]*?)```/g;
    const parts = [];
    let lastIndex = 0;
    let match;

    while ((match = codeBlockRegex.exec(text)) !== null) {
      if (match.index > lastIndex) {
        parts.push(text.slice(lastIndex, match.index));
      }
      const language = match[1] || 'javascript';
      const code = match[2].trim();
      parts.push(
        <CodeBlock key={match.index}>
          <Editor
            value={code}
            onValueChange={() => {}}  // The editor is read-only
            highlight={(code) => Prism.highlight(code, Prism.languages[language], language)}
            padding={10}
            style={{
              fontFamily: '"Fira code", "Fira Mono", monospace',
              fontSize: 14,
            }}
            readOnly
          />
        </CodeBlock>
      );
      lastIndex = match.index + match[0].length;
    }

    if (lastIndex < text.length) {
      parts.push(text.slice(lastIndex));
    }

    return parts;
  };

  return (
    <MessageWrapper isUser={isUser}>
      <Message isUser={isUser}>{formatContent(content)}</Message>
    </MessageWrapper>
  );
});

const MessageList = ({ messages }) => {
  const messagesEndRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  return (
    <MessageListContainer>
      {messages.map((message, index) => (
        <MemoizedMessage
          key={index}
          content={message.content}
          isUser={message.role === 'user'}
        />
      ))}
      <div ref={messagesEndRef} />
    </MessageListContainer>
  );
};

export default memo(MessageList);
