import React from 'react';
import styled from 'styled-components';

const MessageWrapper = styled.div`
  display: flex;
  justify-content: ${({ role }) => (role === 'user' ? 'flex-end' : 'flex-start')};
  padding: 10px;
`;

const MessageBubble = styled.div`
  background-color: ${({ role, theme }) => (role === 'user' ? theme.colors.mutedGreen : 'transparent')};
  color: ${({ role, theme }) => (role === 'user' ? 'white' : theme.colors.text)};
  padding: 10px 15px;
  border-radius: 20px;
  max-width: 60%;
  text-align: ${({ role }) => (role === 'user' ? 'right' : 'left')};
  border: ${({ role }) => (role === 'assistant' ? '1px solid #3D3D3D' : 'none')};  // Optional border for bot messages
`;

const MessageList = ({ messages }) => {
  return (
    <div>
      {messages.map((message, index) => (
        <MessageWrapper key={index} role={message.role}>
          <MessageBubble role={message.role}>
            {message.content}
          </MessageBubble>
        </MessageWrapper>
      ))}
    </div>
  );
};

export default MessageList;
