import React, { useState, useEffect, useRef } from 'react';
import styled from 'styled-components';
import MessageList from './MessageList';
import MessageInput from './MessageInput';
import AIThinkingIndicator from './AIThinkingIndicator';
import axios from 'axios';

const ChatWindowWrapper = styled.div`
  display: flex;
  flex-direction: column;
  height: 100vh;
  background-color: ${props => props.theme.colors.background};
  color: ${props => props.theme.colors.text};
`;

const MessagesWrapper = styled.div`
  flex-grow: 1;
  overflow-y: auto;
  padding: 10px;
  display: flex;
  flex-direction: column;
  justify-content: flex-start;
  box-sizing: border-box;
`;

const ChatWindow = () => {
  const [messages, setMessages] = useState([]);
  const [sessionId, setSessionId] = useState(null);
  const [isAiThinking, setIsAiThinking] = useState(false);
  const messagesEndRef = useRef(null);

  const sendMessage = async (content) => {
    try {
      setMessages(prevMessages => [...prevMessages, { role: 'user', content }]);
      setIsAiThinking(true);

      const response = await axios.post('http://localhost:8000/chat', {
        session_id: sessionId,
        message: content
      });

      setIsAiThinking(false);
      setMessages(prevMessages => [...prevMessages, { role: 'assistant', content: response.data.message }]);
      setSessionId(response.data.session_id);
    } catch (error) {
      console.error('Error sending message:', error);
      setIsAiThinking(false);
      setMessages(prevMessages => [...prevMessages, { role: 'assistant', content: 'Error: Could not send message' }]);
    }
  };

  useEffect(() => {
    if (messagesEndRef.current) {
      messagesEndRef.current.scrollIntoView({ behavior: 'smooth' });
    }
  }, [messages, isAiThinking]);

  return (
    <ChatWindowWrapper>
      <MessagesWrapper>
        <MessageList messages={messages} />
        {isAiThinking && <AIThinkingIndicator />}
        <div ref={messagesEndRef} />
      </MessagesWrapper>
      <MessageInput onSendMessage={sendMessage} />
    </ChatWindowWrapper>
  );
};

export default ChatWindow;
