const theme = {
    colors: {
      primary: '#3D3D3D',           // Muted dark gray for general elements
      secondary: '#3D3D3D',         // Lighter gray for accents like buttons
      background: '#000000',        // Black background
      inputBackground: '#000000',   // Darker gray for input fields
      text: '#FFFFFF',              // White text
      mutedGreen: '#6B8E23',        // Muted green for the "Send" button
      buttonBackground: '#B0B0B0',  // Light gray for general buttons
    }
  };

  export default theme;
